mapper.select:
SELECT interim_report_id, proposal_id, student_id, advisor_id, interim_report_file_id, status, submitted_at, updated_at, deleted
FROM interim_reports
WHERE interim_report_id = #{interimReportId}

mapper.update:
UPDATE interim_reports
SET proposal_id = #{proposalId}, student_id = #{studentId}, advisor_id = #{advisorId}, interim_report_file_id = #{interimReportFileId}, status = #{status}, submitted_at = #{submittedAt}, updated_at = #{updatedAt}, deleted = #{deleted}
WHERE interim_report_id = #{interimReportId}

UPDATE interim_reports
SET deleted = 1
WHERE interim_report_id = #{interimReportId}
