mapper.select:
SELECT user_id, username, password, email, created_at, updated_at, deleted
FROM users
WHERE user_id = #{userId}

mapper.update:
UPDATE users
SET username = #{username}, password = #{password}, email = #{email}, deleted = #{deleted}
WHERE user_id = #{userId}

mapper.delete:
UPDATE users
set deleted = 1
WHERE user_id = #{userId}

mapper.select:
SELECT user_id, username, password, email, created_at, updated_at, deleted
FROM users
WHERE user_id = #{userId}

SELECT user_id, username, password, email, created_at, updated_at, deleted
FROM users
WHERE username = #{username}
